// Extract message content
const messageData = msg.payload;

// Skip if not a text message
if (!messageData || !messageData.text) {
    return null;
}

// Get current time for logging
const now = new Date();
msg.formattedTime = now.toLocaleString();

// Check for Chinese format inquiry
const text = messageData.text;

// Look for the Chinese format pattern
// ÂßìÂêçÔºöxxx
// ÈõªË©±Ôºöxxx
// Êü•Ë©¢‰∫ãÈ†ÖÔºöxxx
const namePattern = /ÂßìÂêç[Ôºö:](.*?)(?=[\n\r]|$)/;
const phonePattern = /ÈõªË©±[Ôºö:](.*?)(?=[\n\r]|$)/;
const inquiryPattern = /Êü•Ë©¢‰∫ãÈ†Ö[Ôºö:](.*?)(?=[\n\r]|$)/;

const nameMatch = text.match(namePattern);
const phoneMatch = text.match(phonePattern);
const inquiryMatch = text.match(inquiryPattern);

// Check if message contains all required elements
if (nameMatch && phoneMatch && inquiryMatch) {
    // Extract the information
    const name = nameMatch[1].trim();
    const phone = phoneMatch[1].trim();
    const inquiry = inquiryMatch[1].trim();

    // Pass the message to the next node
    msg.payload = {
        chatId: messageData.chatId,
        text: messageData.text,
        name: name,
        phone: phone,
        inquiry: inquiry,
        isInquiry: true,
        timestamp: now.toISOString()
    };

    return [msg, null];
} else {
    // Not an inquiry, pass to second output
    return [null, msg];
}

// Get admin numbers from global context or use defaults
const adminNumbers = global.get('adminNumbers') || ['85290897701@c.us']; // Default to your number for testing

// Create a copy of the message for each admin
const messages = [];

// Create a clean message object for each admin
for (const adminNumber of adminNumbers) {
    const adminMsg = {
        payload: {
            body: {
                chatId: adminNumber,
                message: `üì© *NEW INQUIRY*\n\nüë§ *Name:* ${msg.payload.name || 'Unknown'}\nüì± *Phone:* ${msg.payload.phone || 'Unknown'}\nüìû *Chat ID:* ${msg.payload.chatId}\n‚è∞ *Time:* ${msg.formattedTime}\n\nüí¨ *Inquiry:*\n${msg.payload.inquiry || msg.payload.text}`
            }
        }
    };
    messages.push(adminMsg);
}

// Prepare confirmation message for the customer
const confirmMsg = {
    payload: {
        body: {
            chatId: msg.payload.chatId,
            message: "Thank you for your inquiry. Our team has been notified and will respond to you shortly."
        }
    }
};

// Return all messages
return [messages, confirmMsg];

// Set admin numbers in global context
// These are the WhatsApp numbers that will receive inquiry notifications
// Format should be full international number with country code, no + sign, followed by @c.us
// Example: 85212345678@c.us for a Hong Kong number +852 1234 5678

global.set('adminNumbers', [
    '85290897701@c.us'  // Your WhatsApp number for testing
]);

// To add more admins, add more numbers in the same format:
// '85212345678@c.us',  // Another admin
// '85287654321@c.us'   // Another admin

return msg;
