// Define keywords and their corresponding responses
const keywordResponses = {
    'product': {
        message: 'Here\'s information about our product:',
        media: 'https://example.com/product.jpg',
        caption: 'Check out our amazing product!',
        type: 'image'
    },
    'service': {
        message: 'Here\'s information about our services:', 
        media: 'https://example.com/services.pdf',
        caption: 'Our services brochure',
        type: 'document'
    },
    'price': {
        message: 'Here\'s our price list:',
        media: 'https://example.com/price-list.pdf',
        caption: 'Current price list',
        type: 'document'
    },
    'catalog': {
        message: 'Here\'s our latest catalog:',
        media: 'https://example.com/catalog.pdf',
        caption: 'Product catalog',
        type: 'document'
    },
    'contact': {
        message: 'üìû Contact Us:\\n\\nüì± Phone: +852 1234 5678\\nüìß Email: info@example.com\\nüìç Address: 123 Business Street, Hong Kong',
        media: 'https://example.com/location.jpg',
        caption: 'Our location',
        type: 'image'
    }
};

// Extract message content
const messageData = msg.payload;

// Skip if not a text message
if (!messageData || !messageData.text) {
    return [null, msg];
}

// Convert message to lowercase for case-insensitive matching
const text = messageData.text.toLowerCase();

// Check for keywords
let response = null;
for (const [keyword, data] of Object.entries(keywordResponses)) {
    if (text.includes(keyword)) {
        response = {
            ...data,
            chatId: messageData.chatId
        };
        break;
    }
}

if (response) {
    msg.payload = response;
    return [msg, null];
}

return [null, msg];

// Prepare the response message
const response = {
    chatId: msg.payload.chatId,
    message: msg.payload.message,
    media: msg.payload.media,
    caption: msg.payload.caption,
    type: msg.payload.type
};

msg.payload = response;
return [msg, null];
